import format from "date-fns/format";
import axios from "axios";
import store from "../store.js";
import { API_BASE } from "../config";

export default {
  data() {
    return {
      CoverLetter: "",
      CoverLetter_url: "",
      file: "",
      name: "",
      empresa: "",
      project: "",
      email: "",
      description: "",
      phone: "",
      sector: "",
      url: "",
      user: "",
      menu: false,
      inputRules: [
        v => !!v || "This field is required",
        v => v.length >= 3 || "Minimum length is 3 characters"
      ],
      loading: false,
      dialog: false
    };
  },
  methods: {
    pickFile() {
      this.$refs.image.click();
    },

    onFilePicked(e) {
      const files = e.target.files;
      if (files[0] !== undefined) {
        this.CoverLetter = files[0].name;
        if (this.CoverLetter.lastIndexOf(".") <= 0) {
          return;
        }
        const fr = new FileReader();
        fr.readAsDataURL(files[0]);
        fr.addEventListener("load", () => {
          this.CoverLetter_url = fr.result;
          this.file = files[0]; // this is an image file that can be sent to server...
        });
      } else {
        this.CoverLetter = "";
        this.file = "";
        this.CoverLetter_url = "";
      }
    },
    submit() {
      if (this.$refs.form.validate()) {
        this.loading = true;
        let formData = new FormData();
        formData.append("file", this.file);

        const project = {
          EmpresaFK: this.project.EmpresaFK,
          PracticaFK: this.project.practicas_id,
          AccountFK: this.user.account_id,
          sector: this.sector,
          Status: "pendiente",
          CoverLetter: this.CoverLetter
        };
        console.log(project);

        axios
          .post(`${API_BASE}/applications/new`, project, {
            headers: {
              "Content-Type": "application/json",
              Authorization: "Bearer " + localStorage.token
            }
          })
          .then(function(response) {
            console.log("response", response);
          })
          .catch(function(error) {
            console.log("error", error);
          });
        // Upload file to server
        axios
          .post(
            `${API_BASE}/applications/upload/` + localStorage.user,
            formData,
            {
              headers: {
                "Content-Type": "multipart/form-data",
                Authorization: "Bearer " + localStorage.token
              }
            }
          )
          .then(function(response) {
            console.log("response", response);
          })
          .catch(function(error) {
            console.log("error", error);
          });
        this.$emit("solicitudEnviada");
      }
      this.loading = false;
      this.dialog = false;
    },

    getUser() {
      const url = `${API_BASE}/user/` + localStorage.user;

      console.log(url);
      axios
        .get(url, {
          headers: {
            "Content-Type": "application/json",
            Authorization: "Bearer " + localStorage.token
          }
        })
        .then(response => {
          this.user = response.data.data;
          console.log("ResponseU", response);
          console.log("User", this.user);
        });
    }
  },

  mounted() {
    axios
      .get(`${API_BASE}/practicas/` + this.$route.params.id, {
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + localStorage.token
        }
      })
      .then(response => {
        console.log(response.data.data);
        this.project = response.data.data;
        const URL = `${API_BASE}/empresas/` + response.data.data.EmpresaFK;
        axios
          .get(URL, {
            headers: {
              "Content-Type": "application/json",
              Authorization: "Bearer " + localStorage.token
            }
          })
          .then(response => {
            console.log(response);
            this.empresa = response.data.data;
            this.getUser();
          });
      });
  }
};
