import format from "date-fns/format";
import axios from "axios";
import store from "../store.js";
import { API_BASE } from "../config";

export default {
  data() {
    return {
      name: "",
      email: "",
      description: "",
      phone: "",
      sector: "",
      url: "",
      menu: false,
      inputRules: [
        v => !!v || "This field is required",
        v => v.length >= 3 || "Minimum length is 3 characters"
      ],
      loading: false,
      dialog: false
    };
  },
  methods: {
    submit() {
      if (this.$refs.form.validate()) {
        this.loading = true;
        const project = {
          empresa: this.name,
          email: this.email,
          phone: this.phone,
          sector: this.sector,
          url: this.url,
          description: this.description
        };

        axios
          .put(`${API_BASE}/empresas/` + localStorage.empresaFK, project, {
            headers: {
              "Content-Type": "application/json",
              Authorization: "Bearer " + localStorage.token
            }
          })
          .then(function(response) {
            console.log(response);
          })
          .catch(function(error) {
            console.log(error);
          });
      }
      this.loading = false;
      this.dialog = false;
      this.$emit("Empresa añadida");
    },
    fetchData() {
      const url = `${API_BASE}/empresas/` + localStorage.empresaFK;
      console.log(url);
      axios
        .get(url, {
          headers: {
            "Content-Type": "application/json",
            Authorization: "Bearer " + localStorage.token
          }
        })
        .then(response => {
          console.log("Response", response);
          this.name = response.data.data.empresa;
          this.email = response.data.data.email;
          this.phone = response.data.data.phone;
          this.sector = response.data.data.sector;
          this.url = response.data.data.url;
          this.description = response.data.data.description;
        });
    }
  },
  computed: {
    formattedDate() {
      console.log(this.due);
      return this.due ? format(this.due, "Do MMM YYYY") : "";
    }
  },
  mounted() {
    this.fetchData();
  }
};
